#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="E:\text\siyuan-media-player\node_modules\.pnpm\ua-parser-js@1.0.40\node_modules\ua-parser-js\script\node_modules;E:\text\siyuan-media-player\node_modules\.pnpm\ua-parser-js@1.0.40\node_modules\ua-parser-js\node_modules;E:\text\siyuan-media-player\node_modules\.pnpm\ua-parser-js@1.0.40\node_modules;E:\text\siyuan-media-player\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/e/text/siyuan-media-player/node_modules/.pnpm/ua-parser-js@1.0.40/node_modules/ua-parser-js/script/node_modules:/mnt/e/text/siyuan-media-player/node_modules/.pnpm/ua-parser-js@1.0.40/node_modules/ua-parser-js/node_modules:/mnt/e/text/siyuan-media-player/node_modules/.pnpm/ua-parser-js@1.0.40/node_modules:/mnt/e/text/siyuan-media-player/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../ua-parser-js/script/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../ua-parser-js/script/cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../ua-parser-js/script/cli.js" $args
  } else {
    & "node$exe"  "$basedir/../ua-parser-js/script/cli.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
